import React, { useContext } from "react";
import { CartContext } from "../context/Context";

const Cart = () => {
  const { cart, removeCart } = useContext(CartContext);
  return (
    <>
      <h3>cart</h3>
      {cart.map((val) => {
        return (
          <>
            <div className="meal-box d-flex justify-content-between p-5">
              <div className="meal-name">
                <p>{val.title}</p>
                <p>{val.description}</p>
              </div>
              <div className="meal-price d-flex align-items-center">
                <p>Â£{val.price}</p>
              </div>
            </div>
            <button onClick={() => removeCart(val.id)}>remove</button>
          </>
        );
      })}
    </>
  );
};

export default Cart;
