import React, { useState } from "react";

const Form1 = () => {
  const [display, setDisplay] = useState(true);
  return (
    <>
      {display ? (
        <form>
          <input type="text" placeholder="type anything & press Enter" />
          <button class="btn" type="submit" onClick={() => setDisplay(false)}>
            Submit
          </button>
        </form>
      ) : null}
    </>
  );
};

export default Form1;
